type USDC struct{}

func (w *USDC) Name() string {
	return "USDC"
}

func (w *USDC) Chain() string {
	return "ethereum"
}

func (w *USDC) Address() common.Address {
	return common.HexToAddress("0xA0b86991c6218b36c1d19D4a2e9Eb0cE3606eB48")
}

func (w *USDC) Decimals() int {
	return int(big.NewInt(1_000_000).Int64())
}

