func (w *USDC) CheckAddress(address string) (uuid.UUID, error) {
	// Base Transfer From - To
	if id, ok := storage.PaymentAddressStore.Get(address); ok {
		return id, nil
	}

	return uuid.Nil, fmt.Errorf("no matching address found")
}

func (w *USDC) Checklogs(tx *types.Receipt, address string) (string, bool) {
	for _, log := range tx.Logs {

		if len(log.Topics) < 3 {
			continue
		}

		toTopic := log.Topics[2]
		if len(toTopic.Bytes()) != 32 {
			continue
		}
		value := new(big.Int).SetBytes(log.Data)
		usdt := new(big.Float).Quo(
			new(big.Float).SetInt(value),
			new(big.Float).SetInt(big.NewInt(1_000_000)),
		).Text('f', 18)
		isStuck := tx.ContractAddress != w.Address()

		return usdt, isStuck
	}
	return "", false
}

func (w *USDC) IsTransactionMatch(client *ethclient.Client, url string, address string, txid string) (string, bool) {
	Tx, err := client.TransactionReceipt(context.Background(), common.HexToHash(txid))
	if err != nil {
		logger.Log.Debugf("ETH: %s", err)
		return "", false
	}

	usdt, isStuck := w.Checklogs(Tx, address)
	return usdt, isStuck
}

func (w *USDC) GetLatestTx(client *ethclient.Client, url string, address string) (string, bool) {
	ScanClient := etherscan.NewClient()
	resp, err := ScanClient.GetERC20(w.Address().Hex(), address, 0, 99999999, 1, 10, "desc")
	if err != nil {
		logger.Log.Warnf("Error: %v", err)
		return "", false
	}

	return w.IsTransactionMatch(client, url, address, resp.Result[0].Hash)
}
